const tasks = $input.all();
const today = new Date().toISOString().split('T')[0];
const yesterday = new Date(Date.now() - 86400000).toISOString().split('T')[0];

let doneYesterday = 0;
let doing = 0;
let blocked = 0;
let overdue = 0;
let total = 0;
let todo = 0;

const overdueList = [];

for (const item of tasks) {
  const task = item.json;
  total++;

  // Count by status
  if (task.Status === 'Done' && task.LastUpdate === yesterday) {
    doneYesterday++;
  } else if (task.Status === 'Doing') {
    doing++;
  } else if (task.Status === 'Blocked') {
    blocked++;
  } else if (task.Status === 'Todo') {
    todo++;
  }

  // Check overdue
  if (task.Status !== 'Done' && task.Deadline < today) {
    overdue++;
    const deadline = new Date(task.Deadline);
    const diff = Math.ceil((new Date() - deadline) / (1000 * 60 * 60 * 24));
    overdueList.push({
      taskId: task.TaskID,
      title: task.Title,
      owner: task.Owner,
      days: diff
    });
  }
}

// Sort overdue by days descending
overdueList.sort((a, b) => b.days - a.days);

return [{
  json: {
    total,
    doneYesterday,
    doing,
    blocked,
    todo,
    overdue,
    overdueList: overdueList.slice(0, 5) // Top 5
  }
}];

